{"changed":true,"filter":false,"title":"after_midnight_1.rb","tooltip":"/easy_5/after_midnight_1.rb","value":"def time_of_day(number)\n  if number.positive?\n    minutes = 0\n    hours = 0\n    while number.positive?\n      minutes += 1\n      if minutes == 60\n        hours += 1\n        if hours == 24\n          hours = 0\n        end\n        minutes = 0\n      end\n      number -= 1\n    end\n  elsif number.negative?\n    minutes = 60\n    hours = 23\n    while number.negative?\n      minutes -= 1\n      if minutes.zero?\n        hours -= 1\n        if hours.negative?\n          hours = 23\n        end\n        minutes = 60\n      end\n      number += 1\n    end\n  elsif number.zero?\n    hours = 0\n    minutes = 0\n  end\n  p format('%02d', hours) + ':' + format('%02d', minutes)\nend\n\n# LS solution\n\nHOURS_PER_DAY = 24\nMINUTES_PER_HOUR = 60\nMINUTES_PER_DAY = HOURS_PER_DAY * MINUTES_PER_HOUR\n\ndef time_of_day_2(delta_minutes)\n  delta_minutes = delta_minutes % MINUTES_PER_DAY\n  hours, minutes = delta_minutes.divmod(MINUTES_PER_HOUR)\n  format('02d:%02d', hours, minutes)\nend\n\ntime_of_day(0)\ntime_of_day(-3)\ntime_of_day(35)\ntime_of_day(-1437) # 00:03\ntime_of_day(3000)\ntime_of_day(800)\ntime_of_day(-4231) # 01:29\n","undoManager":{"mark":96,"position":100,"stack":[[{"start":{"row":38,"column":18},"end":{"row":38,"column":19},"action":"insert","lines":["t"],"id":627},{"start":{"row":38,"column":19},"end":{"row":38,"column":20},"action":"insert","lines":["i"]},{"start":{"row":38,"column":20},"end":{"row":38,"column":21},"action":"insert","lines":["v"]},{"start":{"row":38,"column":21},"end":{"row":38,"column":22},"action":"insert","lines":["e"]},{"start":{"row":38,"column":22},"end":{"row":38,"column":23},"action":"insert","lines":["?"]}],[{"start":{"row":37,"column":2},"end":{"row":38,"column":0},"action":"insert","lines":["",""],"id":628},{"start":{"row":38,"column":0},"end":{"row":38,"column":2},"action":"insert","lines":["  "]}],[{"start":{"row":39,"